CREATE DATABASE stockuser CHARACTER  SET utf8;USE stockuser;create table users (  user_id int unsigned not null auto_increment,  name varchar(20),  email varchar(60) not null unique,  pass char(40) not null,  registration_date datetime not null,  role char(20) not null,  balance int(11),  primary key (user_id))ENGINE = INNODB;create table threads(  thread_id int unsigned not null auto_increment,  user_id int unsigned not null,  subject varchar(150) not null,  body_t text not null,  primary key (thread_id),  index (user_id))ENGINE = INNODB;create table posts(  post_id int unsigned not null auto_increment,  parent_id int unsigned not null default 0,  thread_id int unsigned not null,  user_id int unsigned not null,  message text not null,  primary key (post_id),  index(parent_id),  index(thread_id),  index(user_id))ENGINE = MYISAM;